export class NavComponent {
  constructor(accountService, router, toaster) {
    this.accountService = accountService;
    this.router = router;
    this.toaster = toaster;
    this.model = {};
    this.registerMode = true;
    this.isAdmin = false;
    this.currentUser$ = this.accountService.currentUser$;
    this.currentAdmin$ = this.accountService.currentAdmin$;
  }

  ngOnInit() {}

  logout() {
    this.accountService.logout();
    location.reload();
  }

  adminLogout() {
    this.accountService.adminLogout();
    this.isAdmin = false;
    location.reload();
  }

  login() {
    this.accountService.Adminlogin(this.model).subscribe(next => {
      this.isAdmin = true;
      this.router.navigateByUrl('/home');
      this.toaster.success('Admin logged in');
    }, error => {
      console.log('failedd admin');

      if (this.isAdmin == false) {
        this.Userlogin();
      }
    });
  }

  Userlogin() {
    this.accountService.login(this.model).subscribe(next => {
      this.router.navigateByUrl('/home');
      this.toaster.success("User logged in");
      location.reload();
    }, error => {
      this.toaster.error(error.error);
    });
  }

  registerToggle() {
    this.registerMode = !this.registerMode;
  }

}

